targets.expr:
process_uptime_seconds{namespace=~"$namespace", app=~"$application", kubernetes_pod_name=~"$pod"}
* on (namespace, pod, container) group_left(uid)
kube_pod_container_info{namespace=~"$namespace", pod=~"$pod"}
* on (namespace, pod) group_left(pod_ip)
kube_pod_info{namespace=~"$namespace", pod=~"$pod"}

(
  max by (uid) (
    kube_pod_info{
      namespace=~"$namespace", 
      app=~"$application", 
      kubernetes_pod_name=~"$pod", 
      pod=~"sf-service-manager-.*"
    }
    * on (uid) group_left()
    (
      count by (uid) (
        kube_pod_info{
          namespace=~"$namespace", 
          app=~"$application", 
          kubernetes_pod_name=~"$pod", 
          pod=~"sf-service-manager-.*"
        }
      ) > 0
    )
    * on (uid) group_left()
    (
      process_uptime_seconds{namespace=~"$namespace", app=~"$application", kubernetes_pod_name=~"$pod"}
      * on (namespace, pod, container) group_left(uid)
      kube_pod_container_info{namespace=~"$namespace", pod=~"$pod"}
      * on (namespace, pod) group_left(pod_ip)
      kube_pod_info{namespace=~"$namespace", pod=~"$pod"}
    )
  ) > 0
)
or
(
  max by (uid) (
    max_over_time(
      kube_pod_info{
        namespace=~"$namespace", 
        app=~"$application", 
        kubernetes_pod_name=~"$pod", 
        pod=~"sf-service-manager-.*"
      }[5m]
    )
  ) * 0
  unless
  max by (uid) (
    kube_pod_info{
      namespace=~"$namespace", 
      app=~"$application", 
      kubernetes_pod_name=~"$pod", 
      pod=~"sf-service-manager-.*"
    }
  )
)

