targets.expr:
http_server_requests_active_seconds_max{namespace=~"$namespace", uri!~"/management/.*"}
* on (namespace, pod) group_left(uid)
kube_pod_info{namespace=~"$namespace", pod=~"$podname"}

tomcat_connections_config_max_connections{namespace=~"$namespace", app=~"$application"}
* on (namespace, pod, container) group_left(uid)
kube_pod_container_info{namespace=~"$namespace"}
* on (namespace, pod) group_left(pod_ip)
kube_pod_info{namespace=~"$namespace"}

avg_over_time(tomcat_connections_keepalive_current_connections{namespace="$namespace", application=~"$application", pod=~"$podname"}[1m])
